    _waitForStageElement() {
        const checkInterval = setInterval(() => {
            // Ê≠£„Åó„ÅÑ„ÇØ„É©„ÇπÂêç„Çí‰ΩøÁî®„Åó„Å¶„Çπ„ÉÜ„Éº„Ç∏„ÅÆ„Ç≠„É£„É≥„Éê„ÇπË¶ÅÁ¥†„ÇíÂèñÂæó
            const stageElement = document.querySelector('[class^="stage_stage_"]');
            //const stageElement = document.querySelector('.stage_stage_1fD7k .stage_frame-wrapper_1JaS_');
            if (stageElement) {
                clearInterval(checkInterval);
                this._createCanvas(stageElement);
            }
        }, 100); // 100ms„Åî„Å®„Å´„ÉÅ„Çß„ÉÉ„ÇØ
    }

    _createCanvas(stageElement) {
        // 2D„Ç≠„É£„É≥„Éê„Çπ„Çí‰ΩúÊàê
        this.canvas = document.createElement('canvas');
        this.canvas.width = stageElement.clientWidth;
        this.canvas.height = stageElement.clientHeight;
        this.canvas.style.position = 'absolute';
        this.canvas.style.top = '0';
        this.canvas.style.left = '0';
        this.canvas.style.zIndex = '1'; // z-index„ÇíË®≠ÂÆö„Åó„Å¶ÂâçÈù¢„Å´Ë°®Á§∫

        // „Çπ„ÉÜ„Éº„Ç∏Ë¶ÅÁ¥†„Å´„Ç≠„É£„É≥„Éê„Çπ„ÇíËøΩÂä†
        stageElement.appendChild(this.canvas);

        // 2D„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà„ÇíÂèñÂæó
        this.ctx = this.canvas.getContext('2d');
        if (!this.ctx) {
            console.error('2D„Ç≥„É≥„ÉÜ„Ç≠„Çπ„Éà„ÅåÂèñÂæó„Åß„Åç„Åæ„Åõ„Çì„ÄÇ');
            return;
        }

        // ËÉåÊôØ„Å®„ÉÜ„Ç≠„Çπ„Éà„ÅÆÊèèÁîª
        if(this.display3D){
            this._drawBackgroundAndText();
        }
    }

    _drawBackgroundAndText() {
        const ctx = this.ctx;
        const width = this.canvas.width;
        const height = this.canvas.height;

        // ÈÄèÊòéÂ∫¶50%„ÅÆÈªí„ÅÑËÉåÊôØ„ÇíÊèèÁîª
        ctx.fillStyle = 'rgba(0, 0, 0, 1)';
        ctx.fillRect(0, 0, width, height);

        // „ÉÜ„Ç≠„Çπ„Éà„ÅÆË®≠ÂÆö
        ctx.font = '48px sans-serif';
        ctx.fillStyle = 'white';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';

        // „ÉÜ„Ç≠„Çπ„Éà„Çí‰∏≠Â§Æ„Å´ÊèèÁîª
        ctx.fillText('hackCraft', width / 2, height / 2);
    }

    _clearCanvas() {
        const ctx = this.ctx;
        ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
    }

